# import packageimport roc_utils as rufrom scipy.stats import kendalltaufrom scipy.stats import timport numpy as npimport pandas as pdfrom sklearn.model_selection import train_test_split# get datadefault_dataset = pd.read_csv('credit_default.csv')# create test datanp.random.seed(42)true_rand_array = np.random.rand(150)y_true = np.round(true_rand_array)y_pred_model_1 = np.random.rand(150)y_pred_model_2 = np.random.rand(150)# calculate the t-statt_stat = ru.roc_t_stat(y_true, y_pred_model_1, y_pred_model_2, kendalltau)# get degrees of freedom and p-valdeg_freedom = len(y_true) - 1p_val = t.sf(abs(t_stat), deg_freedom) * 2